    while(ch>'9'||ch<'0'){if(ch=='-')f=-f;ch=getchar();}
    while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();} 
    return x*f;
}
int n,sum,mi=0,ma=-99999;
int a[mn],b[mn];
int f[mn];
int main(){
