				maxn=cur;//找最大值
			}
			if(cur>=0)//判断是否需要剪枝
			{
				q.push(cur); 
			}
			cur=q.front() ;
		}
		q.pop();
		if(k>=0)q.push(k);
		q.push(-100001);//还是那个特殊值
	}
	printf("%lld",maxn);
	return 0;
}
