bool pd(); //判断函数
int main()
{
	cin>>n;
	for(int i=1;i<=n;i++) cin>>a[i];
	if(!pd()) //如果不全是负数，就贪心
	{
		for(int i=1,x=0;i<=n;i++)
		{
			if(x+a[i]<0) x=0;
			else x+=a[i];
			ans=max(ans,x);
		}
		cout<<ans;
	 	return 0;
	}
	else //否则，找最大的
	{
		for(int i=1;i<=n;i++) ans=max(ans,a[i]);
		cout<<ans;
		return 0;
	}
	return 0;
}
bool pd()
{
	for(int i=1;i<=n;i++) if(a[i]>=0) return false; //如果不全是负数，就return false
	return true; //如果全是负数，就return true
}
