        if(a[i]>temp) temp=a[i];//设置这个是为了防止全是负数，此时sum[i]-minn计算出的子串长度为零这时最大，但是子串最小长度为1，故用这个变量去优化 
    //    cout<<maxn<<' ';
    }
    if(maxn==0) cout<<temp<<endl;
    else cout<<maxn<<endl;
}
