 i:=l;j:=r;
 mid:=v[(i+j) div 2];
 repeat
  while v[i]<mid do inc(i);
  while v[j]>mid do dec(j);
  if i<=j then begin
   t:=v[i];v[i]:=v[j];v[j]:=t;
   inc(i);dec(j);
  end;
 until i>j;
 if l<j then kp(l,j);
 if i<r then kp(i,r);
end;
begin
  readln(n);
  for ii:=1 to n do read(a[ii]);
  readln;
  v[1]:=a[1];
  for ii:=2 to n do
   v[ii]:=max(a[ii],v[ii-1]+a[ii]);
  kp(1,n);
  writeln(v[n]);
end.
