#include <bits/stdc++.h>
using namespace std;

int main() {
    int n = 0;

    cin >> n;
    int a[n];

    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    int cnt = 0;
    for (int j = 0; j < n-1; ++j) {
        for (int i = 0; i < n-j-1; ++i) {
            if (a[i] > a[i+1]) {
                swap(a[i], a[i+1]);
                cnt++;
            }
        }
    }

    cout << cnt << endl;
    return 0;
}
```


