#include<iostream>
#include<cstring>
using namespace std;

const int maxn=1e4+7;
struct PreSegTree
{
	int sum;
	int L,R;
}PST[maxn*16];
int root[maxn],cnt;

void insert(int&now,int l,int r,int x)
{
	PST[++cnt]=PST[now];
	now=cnt;
	++PST[now].sum;
	if(l==r) return;
	int mid=(l+r)>>1;
	if(x<=mid)
		insert(PST[now].L,l,mid,x);
	else if(x>=mid+1)
		insert(PST[now].R,mid+1,r,x);
}

int query(int i,int j,int l,int r,int ql,int qr)
{
	if(ql<=l&&r<=qr)
		return PST[j].sum-PST[i].sum;
	int mid=(l+r)>>1;
	int ans=0;
	if(ql<=mid)
		ans+=query(PST[i].L,PST[j].L,l,mid,ql,qr);
	if(qr>=mid+1)
		ans+=query(PST[i].R,PST[j].R,mid+1,r,ql,qr);
	return ans;
}

int main()
{
