    void Init()
    {
        memset(c,0,sizeof(c));
        return;
    }
    void Add(int i, int j)
    {
        for(; i <= n; i+=lowbit(i))
            c[i] += j;
        return;
    }    
    int Sum(int i)
    {
        int ans = 0;
        for(; i; i-=lowbit(i))
            ans += c[i];
        return ans;
    }
};
