using namespace std;
#define R register
#define N 505
#define inf 707406378
inline void in(int &x) {
    static int ch; static bool flag;
    for(flag = false,ch = getchar();ch < '0'||ch > '9';ch = getchar()) flag |= ch == '-';
    for(x = 0;isdigit(ch);ch = getchar()) x = (x<<1) + (x<<3) + ch - '0';
    x = flag ? -x : x;
}
inline void write(int x){
    if(x<0) putchar('-'),x=-x;
    if(x>9) write(x/10);
    putchar(x%10+'0');
}
int n,m,p; 
int Ti[N];
int dis[N][N];
inline void up(int k){
    for(R int i=0;i<n;++i)
        for(R int j=0;j<n;++j)
            dis[i][j]=min(dis[i][j],dis[i][k]+dis[k][j]);
}
inline int dy(){
    in(n),in(m);
    for(R int i=0;i<n;++i)in(Ti[i]);
    for(R int i=0;i<n;++i){
        for(R int j=0;j<n;++j)dis[i][j]=inf;
        dis[i][i]=0;
    }
    for(R int i=1;i<=m;++i){
        R int a,b,c;in(a),in(b),in(c);
        dis[a][b]=dis[b][a]=c;
