    while(m--){
        R int x,y,t;in(x),in(y),in(t);
        while(Ti[p]<=t && p<n)up(p),p++;
        if(Ti[x]>t || Ti[y]>t || dis[x][y]>=inf)write(-1);
        else write(dis[x][y]);
        putchar('\n');
    }
    exit(0);
}
