bool M[N];
bool cmp(query x,query y){
	if(x.t!=y.t)
		return x.t<y.t;
	if(x.l!=y.l)
		return x.l<y.l;
	return x.r<y.r;
} //为了让询问有序到满足自己的强迫症
bool cmn(node x,node y){
	return x.t<y.t;
} int main(){
	scanf("%d%d",&n,&m);
	for(int i=0;i<n;i++){
		scanf("%d",&p[i].t);
		p[i].D=i;
	} for(int i=0;i<n;i++)
		for(int j=0;j<n;j++)
			e[i][j]=INT_MAX;
	for(int i=1;i<=m;i++){
		scanf("%d%d%d",&a,&b,&c);
		e[a][b]=e[b][a]=c;
	} scanf("%d",&qu);
	for(int i=1;i<=qu;i++){
		scanf("%d%d%d",
		&q[i].l,&q[i].r,
		&q[i].t);
		q[i].D=i;
	} sort(p,p+n,cmn);
	sort(q+1,q+qu+1,cmp);
	int tp=0;
	for(int i=1;i<=qu;i++){
		while(tp<n&&p
		[tp].t<=q[i].t){
			int J,K,T=p[tp].D;
			M[T]=1;
			for(int j=0;j<tp;j++){
				J=p[j].D;
				for(int k=0;k<tp;k++){
					K=p[k].D;
					if(e[J][K]==INT_MAX
					||e[J][T]==INT_MAX)
						continue;
					e[T][K]=e[K][T]=min(e[K][T],
					e[J][K]+e[J][T]);
				}
			} for(int j=0;j<tp;j++){
				J=p[j].D;
				for(int k=0;k<tp;k++){
					K=p[k].D;
					if(e[J][T]==INT_MAX
					||e[T][K]==INT_MAX)
						continue;
					e[J][K]=min(e[J][K],
					e[J][T]+e[T][K]);
				}
			} tp++;
		} if(!M[q[i].l]||!M[q[i].r]
		||e[q[i].l][q[i].r]==INT_MAX){
			ans[q[i].D]=-1;
		} else ans[q[i].D]=e[q[i].l][q[i].r];
	} for(int i=1;i<=qu;i++){
		printf("%d\n",ans[i]);
	} return 0;
}
```
