{
	T=Rein();
	while(T--)
	{
		N=Rein(),M=Rein();
		for(int i=1;i<=N;i++)
			for(int j=1;j<=M;j++)
				sq[i][j]=Rein();
		Dfs(0,1,0);//这里原来是Dfs(0,1,1)
		printf("%d\n",ans);
		ans=-1;
	}
	return 0;
