//修改后的Dfs
void Dfs(int X,int x,int y)
{
	bool poi_rem=false;
	for(int i=y+1;i<=M;i++)//先搜完当前行
	{
		if(!vis[x][i])
		{
			poi_rem=true;
			Vis(x,i);
			Dfs(X+sq[x][i],x,i);
			D_vis(x,i);
		}
	}
	for(int i=x+1;i<=N;i++)
	{
		for(int j=1;j<=M;j++)
		{
			if(!vis[i][j])
			{
				poi_rem=true;
				Vis(i,j);
				Dfs(X+sq[i][j],i,j);
				D_vis(i,j);
			}
		}
	}
	if(!poi_rem&&ans<X)//这里不知道有没有用但还是改了
	{
		ans=X;
	}
}
```

