int n,m,ans;
int a[15][15]；
int dx[9]={0,1,1,1,0,0,-1,-1,-1},dy[9]={0,1,-1,0,-1,1,0,1,-1};//方向增量
int can[15][15];//表示是否可选
void DFS(int i,int j,int now){//i为行，j为列，now为现值
    if(j>m){//列超出，行+1，列归1
        i++;
