        for(k=1;k<9;k++)can[i+dx[k]][j+dy[k]]++;/*此次不能用bool存储，可能有多重状态*/
        DFS(i,j+2,now+a[i][j]);
        for(k=1;k<9;k++)can[i+dx[k]][j+dy[k]]--;
    }
    DFS(i,j+1,now);//不选
}
int main(){
