```cpp
#include<iostream>
#include<cmath>
#include<cstring>
#include<algorithm>
#define MAXN 100005
using namespace std;
char a[MAXN];
int n;
bool isprime(int x)
{
    if(x==0||x==1) return false;
    for(int i=2;i<=sqrt(x);i++)
        if(x%i==0) return false;
    return true;
}
//判断素数（大佬们肯定会，初学者看看就懂）
int main()
{
    cin>>a;//输入字符串
    n=strlen(a);//求字符串的长度
    sort(a,a+n);//给字符串排序（可省略）

    //下面是用哈希法
    int num['z']={0};
    for(int i=0;i<n;i++) num[a[i]]++;
    int maxn=0,minn=MAXN;

    //找出出现次数最多的（maxn），出现次数最少的（minn）
    for(int i='a';i<'z';i++)
    {
        maxn=max(maxn,num[i]);
        if(num[i]!=0) minn=min(minn,num[i]);
    }

    //判断素数，直接调用子函数！
    if(isprime(maxn-minn)) cout<<"Lucky Word"<<endl<<maxn-minn;
    else cout<<"No Answer"<<endl<<0;
}
