int dfs(int k,int s,int ct,int val,int flag){
    if(fg[k][s][ct][val][flag]!=0) return fg[k][s][ct][val][flag];
    if(ct==n){
        if(flag&&val>s) return fg[k][s][ct][val][flag]=f[ct].a[val];
        if(!flag&&val<s) return fg[k][s][ct][val][flag]=f[ct].a[val];
