{
    cin>>n;
    int i,j,k;
    for(i=1;i<=n;i++)
    {
        if(sum>10)//只用各位
        {
            sum%=10;
        }
        if(i%5==0)
        {
            k=i;
            while(k%5==0)//除去5并统计其个数
            {
                k/=5;
                ans++;
            }
        } 
        else
        {
            k=i;
        }
        sum*=k;
    } 
    sum%=10;
    for(i=1;i<=ans;i++)//将2全部除去，同样用个位思想，为了思路明确，使用各个数特判
    {
        if(sum==2)
        {
            sum=6;
            continue;
        } 
        if(sum==4) 
        {
            sum=2;
            continue;
        }
        if(sum==8) 
        {
            sum=4;
            continue;
        }
        if(sum==6)
        {
            sum=8;
            continue;
        }
    } 
    cout<<sum;
    return 0;
}
