void spfa(int k)
{
    d[k]=0;//起始点到自己路径为0 
    V[k]=true;//标记已进队列 
    q.push(k);//进队列 
    while(!q.empty())
    {
        int x=q.front();
        q.pop();//取队列头并出队列 
        V[x]=false;//标记不在队列 
        for(int p=H_ead[x];p;p=N_ext[p])
