            int y=e[p];
            if(d[y]>d[x]+1)//每走一条路长度为1 
            {
                d[y]=d[x]+1;//改变最短路径（松弛） 
                if(!V[y])
                {
                    q.push(y);//入队列 
                    V[y]=true;//标记已进队列 
                }
            }
        }
    }
}
