inline void addedge(int from,int to,int len)
{
    static int cnt=0;
    t[++cnt]=to;
    l[cnt]=len;
    nxt[cnt]=s[from];
    s[from]=cnt;
}
inline void spfa(int st)
{
    static queue<int> q;
    memset(dis,0x3f,sizeof(dis));
    dis[st]=1;
    inq[st]=1;
    q.push(st);
    while(!q.empty())
    {
