```cpp
#include <bits/stdc++.h>
#define INF 0x3f3f3f3f
#define maxn 510
using namespace std;

int k[maxn],G[maxn][maxn];
int n,a,b;

void build(int n)
{
	for(int i=1;i<=n;i++)
	{
		if (i - k[i] > 0)
			G[i][i-k[i]] = 1;
		if (i + k[i] < n+1)
			G[i][i+k[i]] = 1;		//如果可以到达就连一条边
	}
	for(int i=1;i<=n;i++)			//提示：第一组坑数据，自己到自己也有一条边。WA*1
		G[i][i] = 0;

}

int main()
{
	memset(G,INF,sizeof(G));
	scanf("%d%d%d",&n,&a,&b);
	for(int i=1;i<=n;i++)
		scanf("%d",&k[i]);
	build(n);				//建图连边
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			for(int k=1;k<=n;k++)
				G[j][k] = min(G[j][k],G[j][i]+G[i][k]);				//三重循环标准floyd
	if (G[a][b] >= INF)
		printf("-1\n");		//不能到达输出-1。WA*2,
	else
		printf("%d\n",G[a][b]);
	return 0;
}
