#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
int x[205],n,a,b,step,ans=1e8;
bool y[205];
void search(int v)
{
    if(step>=ans) return;
    if(v==b) {ans=min(ans,step);return;}
    y[v]=true;step++;
    if(v+x[v]<=n && !y[v+x[v]]) search(v+x[v]);
    if(v-x[v]>=1 && !y[v-x[v]]) search(v-x[v]);
    step--;y[v]=false;
}
