#include <iostream>
#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <cmath>
#include <algorithm>
using namespace std;
const int M=35;
 struct ss
{
	char h,from,to;	
};
ss ans[M],res[M];
char a[M],b[M],c[M],d[M];
int f[M],n,tot,t,topa,topb,topc,topd;
bool p;

 void change(char q,int &fro,int &too,int &t)
{
	char r;
	if (q=='A') r=a[fro];
	else if (q=='B') r=b[fro];
	else r=c[fro];
	too++; fro--;
	res[t].h=r; res[t].from=q; res[t].to='D'; 
}
 void pd(int &topa,int &topb,int &topc,int &topd,int &t)
{
	bool flag=true;
	while (flag)
	{
		flag=false;
		if (topd>n)
		{
			if (t<tot)
			{
				tot=t; p=true;
				for (int i=1; i<=tot; i++) ans[i]=res[i];
			}
			return;
		}
		if (a[topa]==d[topd])
		{
			t++; change('A',topa,topd,t); flag=true;
		}
		if (b[topb]==d[topd])
		{
			t++; change('B',topb,topd,t); flag=true;
		}
		if (c[topc]==d[topd])
		{
			t++; change('C',topc,topd,t); flag=true;
		}
	}
}
 int find(char s)
{
	if (s=='0') return 27;
	return f[s-96];
}
 void ss(int topa,int topb,int topc,int topd,int t)
{
	int i,j;
