					for (j=0; j<=n; j++) stc[j]=c[j];
					topc++; c[topc]=a[topa]; topa--;
					ss(topa,topb,topc,topd,t);
					for (j=0; j<=n; j++) c[j]=stc[j];
					topa++; topc--;
