			break;
			case 3:
				if (topb>0 && find(b[topb])<find(c[topc]))
				{
					res[t].h=b[topb]; res[t].from='B'; res[t].to='C';
					for (j=0; j<=n; j++) stb[j]=b[j];
					for (j=0; j<=n; j++) stc[j]=c[j];
