struct node {
    int x,y ;
};
node a[701] ;
int n,A1,A2,B1,B2,sum,ans ;
double xx,yy,k,b ;

int main() 
{
    scanf("%d",&n) ;
    for(int i=1;i<=n;i++) 
        scanf("%d%d",&a[i].x,&a[i].y) ;
    ans = min(n,2) ; 
    for(int i=1;i<=n-1;i++) 
        for(int j=i+1;j<=n;j++) 
        {
            A1 = a[j].y-a[i].y ;
            B1 = a[i].x-a[j].x ;
            sum = 2 ;
            for(int k=1;k<=n;k++) 
            {
                if(k==i||k==j) continue ;
                A2 = a[k].y-a[i].y ;
                B2 = a[i].x-a[k].x ;
                if( A1*B2==A2*B1 ) sum++ ; 
            } 
            if(sum>ans) ans = sum ;
        }
    printf("%d\n",ans) ;
    return 0 ; 
}

