    cin>>n>>s>>m;
    len=s.length();
    for (i=1; i<=len; i++)
    if (isalpha(s[i-1])) a[i]=s[i-1]-55;
    else if (isdigit(s[i-1])) a[i]=s[i-1]-48;
    for (i=len,p=1; i>=1; i--,p*=n) num+=a[i]*p;
