int k;
void pwrite(int s){
    if(s<10){putchar(s+'0');return;}
    pwrite(s/10);
    putchar(s%10+'0');
}
void pread(int &k){
    k=0;
    char c=getchar();
    while(c>='0'&&c<='9'){
        k=k*10+c-'0';
        c=getchar();
    }
}
void kill(int s,int now,int p){
    if(p==0){
        pwrite(s);
        exit(0);
    }
//    printf("尝试%d 剩下%d个坏人 现在第%d人\n",s,p,now);
    now=(now+s-2)%(k+p)+1;
    if(now>k)kill(s,now,p-1);
}
int main(){
    pread(k);
    for(int i=k+1;;++i)kill(i,1,k);
    return 0;
}
