long long int array[maxn];

int main()
{
    long long int n;
    cin>>n;
    for(int i=1;i<=n;i++) array[i]=array[i-1]+i;    
    for(int i=1;i<=n;i++)
    {
        long long int mid=array[i-1]+n;
        long long int a=lower_bound(array,array+n+1,mid)-array;
        if(array[a]-array[i-1]==n) 
        {
        if(i!=a) cout<<i<<" "<<a<<endl;
        }
    }
