    n*=2;
    for (int i=2000;i>1;i--)
        if (n%i==0&&i*i<=n&&(n/i-i+1)%2==0)
            printf("%d %d\n",(n/i-i+1)/2,(n/i-i+1)/2+i-1);
    return 0;
