#include<bits/stdc++.h>
using namespace std;
int main()
{
    int m;
    scanf("%d",&m)  //按题设输入
    for(int i=1;i<=m/2;i++)  //此处枚举首项，最大为m/2
    {
        int l=i,r=m/2+1;  //左边界和右边界
        while(l<=r)  //二分
        {
            int mid=((r-l)>>1)+l;  //l,r的中间值（防止爆int的方法）
            long long h=((long long)(i+mid)*(long long)(mid-i+1))>>1;
            if(h>m)  r=mid-1;
            else if(h<m)  l=mid+1;
            else
            {
                printf("%d %d\n",i,mid);
                break;
            }
        }
    }
    return 0;
}
```

