readln(m);
k:=0;//检测是否有解
for i:=1 to m do begin//第一个数从第一个开始枚举
  sum:=i;
  for j:=i+1 to m do begin//依次往后加直到大于或等于所给数
  sum:=sum+j;
  if sum<m then continue//若不等则i值加1继续枚举
  else if sum=m then begin
    inc(k);
    a[k].x:=i;
    a[k].y:=j;
    end
    else begin break; end;//若等于则储存值然后值加1继续循环
    end;
end;
if k<>0 then//若有解则输出
for i:=1 to k do
writeln(a[i].x,' ',a[i].y);
end.
