int main(int argc, char *argv[])  
{  
    std::cin >> n;  
    for (long long i = 1; i <= n; i++)  
    {  
        for (j = i + 1; j < n; j++)  
        {  
            sum = (i + j) * (j - i + 1) / 2;  
            // 自然数求和公式  
            // #1 Sn = n * (n + 1) / 2  
            // #2 Smn = (n + m)(n - m + 1) / 2   
            if (sum >= n)   
                break;  
        }  
        if (sum == n)  
            std::cout << i << ' ' << j << std::endl;  
    }  
} 

