    while((readc=getchar())>=48&&readc<=57) n=n*10+readc-48;
}
void readcard(int &n){
    while((readc=getchar())<48||readc>'z');
    switch(readc){
        case 'H':
            n=1;
            break;
        case 'S':
            n=2;
            break;
        case 'D':
            n=3;
            break;
        case 'C':
            n=4;
            break;
    }
    while((readc=getchar())>=48&&readc<='z') n=n*10+readc-48;
    if(n/100==0) {
        int temp=n%10;
        n/=10;
        n*=100;
        n+=temp;
    }
}
