int y,o=0,u=-1,g=0,t=0,w=0;//临时变量
int f[14]={0,-50,-2,-3,-4,-5,-6,-7,-8,-9,-10,-20,-30,-40};
int k[5]={0};//kj是第j个人的分数，每一次操作都在kj上进行
int chazhao(char zf1,int sz1)
{
		if(zf1=='H')
		{
			b++;
			if(b>0&&b<=13)
			{
				o+=f[sz1];
			}
		}
		if(zf1=='S')
		{
			o-=100;
			u+=2;   //-1+2=1or-2+2=0
		}
		if(zf1=='D')
		{
			o+=100;
			u-=1;   //-1-1=-2or-1+1=0
		}
		if(zf1=='C')
		{
			y=1;
			if(u==-1&&b==0&&n==1)//这个条件要仔细看题
			{//只有一个数C10
				o=25;//25*2=50
			}
		}
		if(b==13)//全部红心都有
		{
			o=200;
			if(u==0)
			{
				o+=500;
			}
		}
	return o;
}
int main()
{
	//freopen("heart.in","r",stdin);比赛一定要写
	//freopen("heart.out","w",stdout);
	char zf;int sz;
	while(1)//多组数据
	{
		b=0;
		for(int i=1;i<=4;i++)
		{
			cin>>n;
			if(n==0)k[i]=0;//没有牌记零分

			for(int j=1;j<=n;j++)
			{
			cin>>zf>>sz;//单独输入字符和数字
			k[i]=chazhao(zf,sz);
			if(b==13)
			{
				b=0;
			}
			if(j==n) 
			{
				if(y==1)
				{
					k[i]*=2;//有C10
				}
				u=-1;o=0;y=0;b=0;//每个人执行前回到初始状态
			}
			if(n==16)
			{
				k[i]=1000;
			}//全部牌都在手里
			}
		}
		if(k[1]==0&&k[2]==0&&k[3]==0&&k[4]==0)break;
		for(int i=1;i<=4;i++)
		{
			if(k[i]>0)
			cout<<"+"<<k[i]<<" ";//正数
			else
			cout<<k[i]<<" ";//一直是按照负数执行的
		}
		cout<<endl;
	}
	//fclose(stdin);fclose(stdout);
	return 0;
} 
