#include <map>
#include <cstdio>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;
const int dx[]={0,-50,-2,-3,-4,-5,-6,-7,-8,-9,-10,-20,-30,-40};
//红记分牌的扣分
map<string,int> b;int ans[7],n[7],a[7][20],i,j;
bool find(int k,int x){
    if (n[k]==0) return false;
    for(int i=1;i<=n[k];i++)
    if (a[k][i]==x) return 1;
    return false;
}
//从第k个人的牌中寻找编号为x的记分牌 
bool check_red(int k){
    for(int i=1;i<14;i++)
    if (!find(k,i))
    return false;
    return true;
}
#define ri register int
//判断第k个人是否拥有所有的红牌 
int calc(int k){
    if (n[k]==16) return 1000;
//	所有记分牌都有，共1000分 
    if (n[k]==0) return 0;
    bool s12=0,d11=0,c10=0;
    register int p=0,ans=0;
    for(ri i=1;i<=n[k];i++){
        if (a[k][i]<14){
            if (!check_red(k)){
                p=1;break;
            }
            else{
                p=2;break;
            }
        }
    }
    //p=0表示没有红牌
    //p=1表示有但没有所有的红牌
    //p=2表示有且有所有的红牌
    for(ri i=1;i<=n[k];i++){
        switch(a[k][i]){
            case 14:d11=1;break;//有D11
            case 15:s12=1;break;//有S12
            case 16:c10=1;break;//有C10
            default:if (p==1) ans+=dx[a[k][i]];
            //有但没有所有的红记分牌，所以扣分
        }
    }
    if (p==2){
        if (d11&&s12) ans=500;
        else{
            ans=200;
            if (s12) ans-=100;
            else if (d11) ans+=100;
        }
    }
    else{
        if (p==0) ans=0;
        if (s12) ans-=100;
        if (d11) ans+=100;
    }
    if (c10){
        if (n[k]==1) ans=50;
        else ans*=2;
    }
    //计算每个人的得分（规则看题目）
    return ans;
}
int main(){
//	freopen("t1.in","r",stdin);
    b["H1"]=1;b["H2"]=2;b["H3"]=3;b["H4"]=4;
    b["H5"]=5;b["H6"]=6;b["H7"]=7;b["H8"]=8;
    b["H9"]=9;b["H10"]=10;b["H11"]=11;
    b["H12"]=12;b["H13"]=13;b["D11"]=14;
    b["S12"]=15;b["C10"]=16;//各记分牌编号 
    memset(n,-1,sizeof(n));
    while (cin>>n[1]){
        if (n[1]==0){
            cin>>n[2];
            if (n[2]==0){
                cin>>n[3];
                if (n[3]==0){
                    cin>>n[4];
                    if (n[4]==0){
                        return 0;
                    }
                }
            }
        }
        //判断是否结束输入
        for(i=1;i<5;i++){
            if (n[i]==-1) cin>>n[i];
            for(j=1;j<=n[i];j++){
                register string s;
                cin>>s;a[i][j]=b[s];
            }
        }
        for(i=1;i<5;i++)
        ans[i]=calc(i);
        for(i=1;i<4;i++){
            if (ans[i]>0) printf("+");
            printf("%d ",ans[i]);
        }
        if (ans[4]>0) printf("+");
        printf("%d\n",ans[4]);
        //输出
        memset(n,-1,sizeof(n));
//		This is very important!
    }
    return 0;
}
