```cpp
#include<iostream>
using namespace std;
int k,flag;  //用flag判断是否有解，默认为0
int sub(int m,int n){
    switch(n){
        case 1:return m/100;
        case 2:return m/10%1000;
        case 3:return m%1000;
    }
}
int main(){
    cin>>k;
    for(int i=10000;i<=30000;++i){ //区间内逐个扫描
        if(!(sub(i,1)%k)&&!(sub(i,2)%k)&&!(sub(i,3)%k)){  //sub1/sub2/sub3全部整除便输出
            cout<<i<<endl;
            flag=1;   //有解则改为1
        }
    }
    if(!flag)   //最后仍为0即为无解
        cout<<"No";
    return 0;
}
```

