		scanf("%d",&now);
		a[abs(now - last)] = true;
		last = now;
	}
	for(int i = 1;i < n;++ i)
	{
		if(a[i] != true)
		{
			cout << "Not jolly" << endl;
			return 0;
		}
	}
	cout << "Jolly" << endl;
	return 0;
}
