    if(t>r) { //如果搜完了就输出a数组
        print();
        return;
    }
    for(int i=1; i<=n; i++){ //枚举每一个数
        if(!b[i]&&i>a[t-1]||t==1)//当i木有被使用过且i必须大于前个数但除1之外
{
            a[t]=i; //将i存入a数组
            b[i]=true;//标记i已被使用
            search(t+1);//继续搜索
            a[t]=0;//回溯一步
            b[i]=false; 
        }
    } 
}
int main(){
    cin>>n>>r;//输入
   search(1);
   return 0;
}
