	for (int i = 0; i <= n; ++i)//第一行永远是a[i] = i,a[0] 的话可以设也可以不设
	{
		a[i] = i;
		/* code */
	}
	while(a[0] == 0){//a[0] 如果进位了就结束
		for (int i = 1; i <= m; ++i)//打印
		{
			cout<<setw(3)<<a[i];//别忘了域宽,我被坑了2次
			/* code */
		}
		cout<<endl;
		a[m] ++;//开始进位
		if (a[m] > n)//这个if不要也行
		{
			int k = m;//k统计进到几位
			while(a[k] > n - (m - k)){//进位
				a[k - 1] ++;
				if (k - 1 == 0)//改变a[0]直接退出
				{
					return 0;
					/* code */
				}
				k --;
			}
			k ++;//每次都多－了一次,加上
			for (int i = k; i <= m; ++i)//进位
			{
				a[i] = a[i - 1] + 1;
				/* code */
			}
			/* code */
		}
	}
	return 0;
}
