```c
#include"iostream"
#include"cstring"
using namespace std;
int t=-1,n,ans=0;
int u[101]={0};//标记该位置有歌
string a[101],b[101],c[101];//歌，动态，队列数组
int main()
{
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		cin>>a[i]>>b[i]; 
	}
	for(int i=1;i<=n;i++)
	{
		if(b[i]=="SAME")
		{
			c[i]=a[i];
			u[i]=1;
		}
	}//标记加入队SAME
	for(int i=1;i<=n;i++)
	{
		if(b[i]=="DOWN")
		{
			t=-1;
			while(t<0)
			{
				ans++;
				if(u[ans]!=1){
					c[ans]=a[i];//入队
					u[ans]=1;//标记
					t=1;
				}
			}
		}
	}//DOWN先放UP就可以从第一位开始入队
	ans=0;
	for(int i=1;i<=n;i++)
	{
		if(b[i]=="UP")
		{
			t=-1;
			while(t<0)
			{
				ans++;
				if(u[ans]!=1){
					c[ans]=a[i];//入队
					t=1;
				}
			}	
		}
	}//UP入队
	for(int i=1;i<=n;i++)
	{
		cout<<c[i]<<endl;
	}
	return 0;	
}
```

