#include<stdio.h>
int main(void)
{
    int right[110000]={0};
    int left[110000]={0};//模拟双向链表 
    int book[110000]={0};//在队列中则为1，方便删除操作的去重。 
    int n,m,i,j,k,p;
    scanf("%d",&n);
    book[1]=right[0]=1;//初始化，right[0]指向第一人(目前来说是编号1，即地址1) 
    for(i=2;i<=n;i++)
    {
        scanf("%d %d",&k,&p);
        book[i]=1;//表示在队列中 
        if(p==0) 
        {    
            right[i]=k;
            left[i]=left[k];
            right[left[k]]=i;
            left[k]=i; //4条指令完成添加元素到k左边的操作。 
        }
        else 
        {
            left[i]=k;
            right[i]=right[k];
            left[right[k]]=i;
            right[k]=i;//对称 
        }
    }
    scanf("%d",&m);
    int count=0;//存储重复删除的次数 
    for(i=1;i<=m;i++)
    {
        scanf("%d",&j);
        if(!book[j]) {count++;continue;} //不在队列中 
        right[left[j]]=right[j];
        left[right[j]]=left[j];
        left[j]=0;
        right[j]=0;//4条指令完成删除操作。 
        book[j]=0;//移出了队列 
    }
    int max=n-m+count,current=0;//因为right[0]存储了队列最前面的编号(即地址)。 
    for(i=0;i<max;i++)
    {
        current=right[current]; //从编号1开始遍历。 
        printf("%d ",current);
    }
    return 0; 
}
