#include <cstdio>

// 我们定义一个结构体作为双向链表的节点。
// front和next分别存放前后两个节点的下标。

struct Node
{
	int front;
	int next;
}ls[112345]; // 多开点hhhh

int hd; // 用来标记第一个元素下标。

void Add(int num, int k, int p)
{
	if (p == 1)
	{
    	// 链表的插入了解一下！这个初赛都要考的啊！！
        // 这里虽然做了一些改变，但是大同小异！
        // 关于指针链表的插入，请去被注释的里面找！
		ls[num].front = k;
		ls[num].next = ls[k].next;
		ls[ls[k].next].front = num;
		ls[k].next = num;
	}
	else
	{
    	// 如果要插入到前面，就找到前一个位置。
        // 然后就和插入到后面一样辣！
		k = ls[k].front;
		ls[num].front = k;
		ls[num].next = ls[k].next;
		ls[ls[k].next].front = num;
		ls[k].next = num;
	}
}

void Del(int num)
{
	//如果front == -1，就表示已经被删除了，不用查找。
	if (ls[num].front != -1)
	{
		ls[ls[num].front].next = ls[num].next;
		ls[ls[num].next].front = ls[num].front;
		ls[num].front = -1;
	}

}

void Print()
{
	// 链表的遍历了解一下！
	for (int i = ls[0].next; i != -1; i = ls[i].next)
	{
		printf("%d ", i);
	}
}

