#include<cstdio>
struct node{
    int n;//保存节点的值
    node *left,*right;//左边右边各一个
    node(int t){//构造函数
        left=right=NULL;
        n=t;
    }
}*p[100010],*q;
int main(){
    int m,n,i,j,k,u=1,v;
    p[1]=new node(1);
    scanf("%d",&n);
    for(i=2;i<=n;i++){
        scanf("%d%d",&j,&k);
        p[i]=new node(i);
        if(k){//插入过程
            if(p[j]->right){
                p[j]->right->left=p[i];
                p[i]->right=p[j]->right;    
            }
            p[j]->right=p[i];
            p[i]->left=p[j];
        }
        else{
            if(p[j]->left){
                p[j]->left->right=p[i];
                p[i]->left=p[j]->left;    
            }
            p[j]->left=p[i];
            p[i]->right=p[j];
            if(j==u)u=i;//用u来记录链表开端
        }
    }
    scanf("%d",&m);
    for(i=1;i<=m;i++){//删除过程
        scanf("%d",&k);
        if(p[k]->left)
            p[k]->left->right=p[k]->right;
        if(p[k]->right){
            p[k]->right->left=p[k]->left;
            if(k==u)
                u=p[k]->right->n;
        }
        p[k]->left=p[k]->right=NULL;
    }
    q=p[u];
    while(q){//循环直到q为空
        printf("%d ",q->n);
        q=q->right;
    }
    return 0;
}

