struct node
{
   int pre,next;
};
node lst[100001];
int n,m,s,p;
int main(){
    cin>>n;
    lst[1].pre=0,lst[1].next=0;
    for(int i=2;i<=n;i++)
    {
        cin>>s>>p;
		if(p==0)  //插到s号的左边
		{
		    lst[lst[s].pre].next=i;	
			lst[i].pre=lst[s].pre;	
			lst[i].next=s;
			lst[s].pre=i;
		}
		else  //插入s号的右边
		{
		    lst[lst[s].next].pre=i;	
			lst[i].next=lst[s].next;
			lst[i].pre=s;
			lst[s].next=i;
		} 
    }
    cin>>m;
    for(int i=1;i<=m;i++)
    {
    	cin>>s;
    	if(lst[s].pre==-1&&lst[s].next==-1)  //此人已被删除 
		  continue; 
    	lst[lst[s].pre].next=lst[s].next;
    	lst[lst[s].next].pre=lst[s].pre;
    	lst[s].pre=-1;
    	lst[s].next=-1;
    }
    for(int i=1;i<=n;i++)
      if(lst[i].pre==0)
      {
      	 s=i;
      	 break;
      }
    do{
    	cout<<s<<" ";
    	s=lst[s].next;
    }while(s>0);
    return 0;
}
