完整代码如下：

```
// luogu-judger-enable-o2
#include <map>
#include <set>
#include <stack>
#include <cstdio>
#include <string>
#include <vector>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <algorithm>

using namespace std;

struct Ndlk
{
    struct Ndlk * last;
    int num;
    struct Ndlk * next;
};

int main()
{
    int n,m,p,k,i,nu;
    while (scanf("%d",&n)!=EOF)
    {
        Ndlk ** arr=(Ndlk **)calloc(n+1,sizeof(Ndlk *));
        arr[0]=NULL;
        Ndlk *tmp;
        int first=1;
        for (i=1;i<=n;i++)
            arr[i]=(Ndlk *)calloc(1,sizeof(Ndlk)),
            arr[i]->num=i ,arr[i]->last=NULL ,arr[i]->next=NULL ;
        for (i=2;i<=n;i++)
        {
            scanf("%d %d",&k,&p);
            if (k==first && p==0)
                first=i;
            if (p==0)
            {
                arr[i]->next=arr[k];
                arr[i]->last=arr[k]->last;
                arr[k]->last=arr[i];
                if (arr[i]->last!=NULL)
                    arr[i]->last->next=arr[i];
            }
            if (p==1)
            {
                arr[i]->last=arr[k];
                arr[i]->next=arr[k]->next;
                arr[k]->next=arr[i];
                if (arr[i]->next!=NULL)
                    arr[i]->next->last=arr[i];
            }
        }
        scanf("%d",&m);
        for (i=0;i<m;i++)
        {
            scanf("%d",&nu);
            if (arr[nu]->num==0) continue;

            if (first==nu) first=arr[nu]->next!=NULL ? arr[nu]->next->num : 0 ;

            if (arr[nu]->last!=NULL)
                arr[nu]->last->next=arr[nu]->next;
            if (arr[nu]->next!=NULL)
                arr[nu]->next->last=arr[nu]->last;
            arr[nu]->next=NULL ,arr[nu]->last=NULL ,arr[nu]->num=0;
        }
        for (tmp=arr[first];tmp->next!=NULL;tmp=tmp->next)
            printf("%d ",tmp->num);
        printf("%d\n",tmp->num);

        for (i=0;i<=n;i++)
            if (arr[i]!=NULL)
                free(arr[i]);
        free(arr);
    }
    return 0;
}
