        a[a[t1].left].right=a[t1].right;
        a[a[t1].right].left=a[t1].left;
        a[t1].alive=0;//交换，并删除
    }
    int head=0;
    for (i=1;i<=n;i++){
        if (a[i].left==-1&&a[i].alive==1){
            head=i;
            break;
        }//寻找开头
    }
    while (a[head].right!=-1){
        cout<<head<<' ';
        head=a[head].right;
    }//依次输出
    cout<<head;//最后一个没被输出
    return 0;
}
```
