	if(ren[x].l==0)return x;//左边木有人这个就是最前面的了~
	else return find_l(ren[x].l);//找左边还有木有人
}
int main()
{
	int n,m,op,x;
	scanf("%d",&n);
	ren[1].l=ren[1].r=0;ren[1].c=1;
	for(int i=2;i<=n;i++)
	{
		scanf("%d%d",&x,&op);
		if(op==0)
		{
			int l=ren[x].l;//十分暴力的。。找到原来左边的人
			ren[l].r=i;//告诉它右边现在是i
			ren[x].l=i;//告诉x左边是i
			ren[i].l=l;//下面懒得打了。。。大约这个意思
			ren[i].r=x;
		}
		else
		{
			int r=ren[x].r;
			ren[r].l=i;
			ren[x].r=i;
			ren[i].l=x;
			ren[i].r=r;
		}
		ren[i].c=i;
	}
	scanf("%d",&m);
	for(int i=1;i<=m;i++)
	{
		int x;
		scanf("%d",&x);
		ren[x].c=0;//被删掉就变0吧。。。（懒得再拆一个又重新拼起来。）
	}
	int root=find_l(1);
	print(root);
	return 0;
}
