double a;
int main(){
  cin>>n;
  for(int i=1;i<=n;i++){
     cin>>a>>t;
    int b=a;
    for(int j=1;j<=t;j++){
      int num=j*b;
      if(ans[num]==0)ans[num]=1;
      if(ans[num]==1)ans[num]=0;
    }
  }
  for(int i=1;i<=5000001;i++){
    if(ans[i]==1){cout<<i;return 0;}
